import React, { useState } from "react";

import "./App.css";

import Intro from "./components/Intro";
import Skills from "./components/Skills";
import Profile from "./components/Profile";
import Projects from "./components/Projects";
import Contact from "./components/Contact";

import { data } from "./data.js";

function App() {
  const [curLang, setCurLang] = useState(navigator.language);

  const changeLanguage = () => {
    if (curLang === "tr") setCurLang("en");
    else setCurLang("tr");
  };

  return (
    <>
      <Intro changeLang={changeLanguage} curLang={curLang} />
      <Skills />
      {curLang === "tr" ? (
        <Profile profile={data.tr.profile} />
      ) : (
        <Profile profile={data.en.profile} />
      )}
      {curLang === "tr" ? (
        <Projects projects={data.tr.projects} />
      ) : (
        <Projects projects={data.en.projects} />
      )}

      <Contact />
    </>
  );
}

export default App;
